PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE goodreads
    (id INTEGER PRIMARY KEY,
    title TEXT,
    author TEXT,
    author_lf TEXT,
    additional_authors TEXT,
    ISBN,
    ISBN13,
    rating INT,
    avg_rating FLOAT,
    publisher TEXT,
    binding TEXT,
    pages INT,
    year_published INT,
    original_publication_year INT,
    date_read DATE,
    date_added DATE,
    review TEXT
    );
COMMIT;
